			function resizeIframes(){
				if(checkClass('post-body')){
				var iframeContainer = document.getElementsByClassName('post-body')[0];
				var resRatio = 9/16;
						var windowSize = iframeContainer.offsetWidth;
				var allIFrames = iframeContainer.getElementsByTagName('iframe');
				for (var i = 0; i < allIFrames.length; i++) {
								if(allIFrames[i].getAttribute('allow') != null){
									if(allIFrames[i].getAttribute('allow').includes('autoplay')){
							allIFrames[i].setAttribute('width', windowSize);
							allIFrames[i].setAttribute('height', windowSize*resRatio);
									}
							}
				}
					}
			}


			function $_GET(param) {
				var vars = {};
				window.location.href.replace( location.hash, '' ).replace(
					/[?&]+([^=&]+)=?([^&]*)?/gi, // regexp
					function( m, key, value ) { // callback
						vars[key] = value !== undefined ? value : '';
					}
				);

				if ( param ) {
					return vars[param] ? vars[param] : null;
				}
				return vars;
			}


			function checkClass(x){
				var theClass = document.getElementsByClassName(x);
				if (theClass.length > 0) {
					return true;
				}
  			    else{
					return false;
				}
			}

			function checkID(x){
				var theID = document.getElementById(x);
				if (theID === null) {
					return false;
				}
        		else{
					return true;
				}
			}

			function checkTag(x){
				var theTag = document.getElementsByTagName(x);
				if (theTag.length > 0) {
					return true;
				}
  			    else{
					return false;
				}
			}

			window.onload = init;
