// Functions for categories
	function OCLabels(n){
   		if(checkClass('Label')){
			var labels = document.getElementsByClassName('Label');
        	var labelContent = labels[n].getElementsByClassName('widget-content')[0].getElementsByTagName('ul')[0];
            if (labelContent.style.display == 'block'){
				labelContent.style.display = 'none';
            } else {
				labelContent.style.display = 'block';
            }
        }
	}

	function fixLabels(){
    	if(checkClass('Label')){
			// Title tag will change into the element of tag name. eg.) h3 will turn h2 tag to h3. Default = 'h2'
			var titleTag = 'h3';
			// The 'labels' used as multible label widgets. label as a 1 label widget.
			var labels = document.getElementsByClassName('Label');
			for(var i = 0; i < labels.length; i++){
        		var label = labels[i];
				// change tag of title.
                if(titleTag != 'h2') {
					label.getElementsByTagName('h2')[0].outerHTML = '<' + titleTag + '>' + label.getElementsByTagName('h2')[0].innerHTML + '</' + titleTag + '>';
                }
				// adding attributes to tag
				var title = label.getElementsByTagName(titleTag)[0];
				title.setAttribute('onclick', 'OCLabels(' + i + ');');
				var labelContent = label.getElementsByClassName('widget-content')[0].getElementsByTagName('ul')[0];
				labelContent.className += 'slideInDown';
				// fixing each list items for number of posts in label.
				// Use 'category' as a single category list item.
				var categories = label.getElementsByClassName('widget-content')[0].getElementsByTagName('ul')[0].getElementsByTagName('li');
              	for(var j = 0; j < categories.length; j++){
					var category = categories[j];
                  	if(category.getElementsByTagName('a')[0] !== undefined){
						if(category.getElementsByTagName('span')[0] !== undefined){
							category.getElementsByTagName('a')[0].innerHTML = category.getElementsByTagName('a')[0].innerHTML + ' ' + category.getElementsByTagName('span')[0].innerHTML;
							category.getElementsByTagName('span')[0].outerHTML = '';
                        }
					}
                  	else {
						if(category.getElementsByTagName('span')[1] !== undefined){
							category.getElementsByTagName('span')[0].innerHTML = category.getElementsByTagName('span')[0].innerHTML + ' ' + category.getElementsByTagName('span')[1].innerHTML;
							category.getElementsByTagName('span')[1].outerHTML = '';
                        }
					}
				}
			}
        }
    }
	fixLabels();
