	function fixAsides(){
		var asides = document.getElementsByTagName('aside');
		for(var i = 0; i < asides.length; i++){
        	var aside = asides[i];
            if(aside.getElementsByTagName('div')[0].getElementsByTagName('div')[0] == null){
				aside.style.padding = '0px';
				aside.style.borderWidth = '0px';
			}

		}
    }
	fixAsides();
