head.script:
    //
        // Functions for categories
        function OCLabels(n){
			const labels = document.getElementById('LabelSection').getElementsByClassName('Label');
			if(labels.length > 0) {
				const labelContent = labels[n].getElementsByClassName('widget-content')[0].getElementsByTagName('ul')[0];
                if (labelContent.style.display == 'block'){
                   	labelContent.style.display = 'none';
                } else {
                	labelContent.style.display = 'block';
                }
			}
        }

		function FixSidebar(){
			const sidebar = document.getElementById('sidebar');
			const MBStyle = window.getComputedStyle(document.getElementById('header-menu'));
			if(sidebar.style.display != 'flex' && MBStyle.display == 'block'){
				sidebar.style.display = 'flex';
			} else if(sidebar.style.display != 'block' && MBStyle.display == 'none'){
                const block1 = document.getElementById('menu-block1');
                const block2 = document.getElementById('menu-block2');
                const block3 = document.getElementById('menu-block3');
        		block1.setAttribute('class', 'rotateLeft');
                block2.setAttribute('class', 'rotateMiddle');
                block3.setAttribute('class', 'rotateRight');
				sidebar.style.display = 'block';
			}
		}

        function fixLabels(){
			const labels = document.getElementById('LabelSection').getElementsByClassName('Label');
			if(labels.length > 0) {
				for(var i = 0; i < labels.length; i++) {
					const label = labels[i];
					// adding attributes to tag
					const title = label.getElementsByClassName('title')[0];
					title.setAttribute('onclick', 'OCLabels(' + i + ');');
                    const labelContent = label.getElementsByClassName('widget-content')[0].getElementsByTagName('ul')[0];
                    labelContent.className += 'slideInDown';
				}
			}
        }

        function menu(){
			const block1 = document.getElementById('menu-block1');
			const block2 = document.getElementById('menu-block2');
			const block3 = document.getElementById('menu-block3');
			const sidebar = document.getElementById('sidebar');
			const SBStyle = window.getComputedStyle(sidebar);
			if(SBStyle.display == 'none'){
        		block1.setAttribute('class', 'rotateLeft');
                block2.setAttribute('class', 'rotateMiddle');
                block3.setAttribute('class', 'rotateRight');
				sidebar.style.display = 'flex';
        	}
        	else{
        		block1.setAttribute('class', 'rotateLeftReturn');
                block2.setAttribute('class', 'rotateMiddleReturn');
                block3.setAttribute('class', 'rotateRightReturn');
				sidebar.style.display = 'none';
        	}
       	}

		function search(){
			const title = document.getElementById("header-title");
			const searchBtn = document.getElementById("searchBtn");
			const backBtn = document.getElementById("backBtn");
			const searchBar = document.getElementById("SearchBar");
			if(title.style.display == "none"){
				title.style.display = "block";
				searchBtn.style.display = "block";
				backBtn.style.display = "none";
				searchBar.style.display = "none";
			}
			else{
				title.style.display = "none";
				searchBtn.style.display = "none";
				backBtn.style.display = "block";
				searchBar.style.display = "block";
			}
		}

		function toClipboard(str) {
			var el = document.createElement('textarea');
            el.setAttribute('readonly', '');
            el.style = {position: 'absolute', left: '-9999px'};
			el.value = str;
			document.body.appendChild(el);
			el.select();
			document.execCommand('copy');
			document.body.removeChild(el);
		}

        function checkClass(x){
             const theClass = document.getElementsByClassName(x);
             if (theClass.length > 0) {
                 return true;
             }
             else{
                 return false;
             }
        }

        function checkID(x){
        	const theID = document.getElementById(x);
        	if (theID === null) {
        		return false;
        	}
            else{
         		return true;
       		}
        }

        function checkTag(x){
        	const theTag = document.getElementsByTagName(x);
        	if (theTag.length > 0) {
        		return true;
        	}
            else{
        		return false;
       		}
        }

