b:if.script:
        //
            function shareButtons() {
                const elements = document.getElementsByClassName('sharing-platform-button');
                if(elements.length > 0) {
                    for(var i = 0; i < elements.length; i++) {
                        if(elements[i].getAttribute('class') == 'sharing-platform-button sharing-element-link') {
                            const link = elements[i].getAttribute('data-url');
                            elements[i].parentElement.addEventListener('click', function (event) {
                                toClipboard(link);
								event.preventDefault();
                            });
                        } else if(elements[i].getAttribute('class') == 'sharing-platform-button sharing-element-other') {
                            elements[i].parentElement.style.display = 'none';
                        } else {
                            const link = elements[i].getAttribute('data-href');
                            elements[i].parentElement.addEventListener('click', function (event) {
                                window.open(link, '_blank', 'height=430, width=640' );
								event.preventDefault();
                            });
                        }
                    }
                    return true;
                } else {
                    return false;
                }
            }
            shareButtons();

			function resizeIframes(){
				const iframeContainer = document.getElementsByClassName('post-body');
				if(iframeContainer.length > 0){
					const resRatio = 9/16;
					const windowSize = iframeContainer[0].offsetWidth;
					const allIFrames = iframeContainer[0].getElementsByTagName('iframe');
					for (var i = 0; i < allIFrames.length; i++) {
						if(allIFrames[i].getAttribute('allow') != null){
							if(allIFrames[i].getAttribute('allow').includes('autoplay')){
								allIFrames[i].setAttribute('width', windowSize);
								allIFrames[i].setAttribute('height', windowSize*resRatio);
							}
						}
					}
				}
			}
       		resizeIframes();

			fixLabels();

